mapper.select:
SELECT let_no,let_send_id,let_receive_id,let_title,let_detail,let_readdate,NVL(let_save,'') let_save
FROM letter
WHERE let_no=#{letNo}

mapper.update:
UPDATE letter

let_readdate = SYSDATE

WHERE let_no = #{letNo:NUMERIC}

		UPDATE letter

		set let_rec_view = 1
		
		WHERE let_no = #{letNo:NUMERIC}

		UPDATE letter

		set let_send_view = 1
		
		WHERE let_no = #{letNo:NUMERIC}
