mapper.select:
  	SELECT COUNT(*)
  	FROM(	SELECT proj_no, proj_user_id, proj_division, proj_title, proj_annostart, proj_annoend, proj_startdate, proj_enddate, proj_location, proj_detail, proj_viewcount
					FROM project
					
						
							
								proj_title = #{searchKeyword}
							
							
								proj_location = #{searchKeyword}
							
						
					 ) countTable

mapper.update:
UPDATE project

	proj_viewcount	=	proj_viewcount+1

WHERE proj_no = #{projNo}

UPDATE project
proj_division = #{projDivision:NUMERIC}, proj_title = #{projTitle:VARCHAR}, proj_annoend = #{projAnnoEnd:DATE}, proj_startdate = #{projStartDate:DATE}, proj_enddate = #{projEndDate:DATE}, proj_location = #{projLocation:VARCHAR}, proj_detail = #{projDetail:VARCHAR}
WHERE proj_no = #{projNo}
