mapper.select:
SELECT * FROM ( SELECT inner_table.* FROM( SELECT p.proj_no, p.proj_user_id, p.proj_division, p.proj_title, p.proj_annostart, p.proj_annoend, p.proj_startdate, p.proj_enddate, p.proj_location, p.proj_detail, p.proj_viewcount, round((TO_DATE(concat(p.proj_enddate,'235959'),'YYYY-MM-DD HH24:MI:SS')-TO_DATE(p.proj_startdate,'YYYY-MM-DD HH24:MI:SS'))) as proj_expectdate, round((TO_DATE(concat(p.proj_annoend,'235959'),'YYYY-MM-DD HH24:MI:SS')-TO_DATE(p.proj_annostart,'YYYY-MM-DD HH24:MI:SS'))) as proj_dday, a.scrap_no FROM project p , (SELECT scrap_no, proj_no FROM scrap WHERE user_id=#{scrapUserId}) a
p.proj_no=a.proj_no(+)
) inner_table)

  	SELECT COUNT(*)
  	FROM(	SELECT proj_no, proj_user_id, proj_division, proj_title, proj_annostart, proj_annoend, proj_startdate, proj_enddate, proj_location, proj_detail, proj_viewcount
					FROM project
					
						
							
								proj_title = #{searchKeyword}
							
							
								proj_location = #{searchKeyword}
							
						
					 ) countTable

mapper.update:
UPDATE project
proj_division = #{projDivision:NUMERIC}, proj_title = #{projTitle:VARCHAR}, proj_annoend = #{projAnnoEnd:DATE}, proj_startdate = #{projStartDate:DATE}, proj_enddate = #{projEndDate:DATE}, proj_location = #{projLocation:VARCHAR}, proj_detail = #{projDetail:VARCHAR}
WHERE proj_no = #{projNo}
