mapper.update:
UPDATE project

   proj_viewcount   =   proj_viewcount+1

WHERE proj_no = #{projNo}

UPDATE project
proj_division = #{projDivision:NUMERIC}, proj_title = #{projTitle:VARCHAR}, proj_annoend = #{projAnnoEnd:DATE}, proj_startdate = #{projStartDate:DATE}, proj_enddate = #{projEndDate:DATE}, proj_location = #{projLocation:VARCHAR}, proj_detail = #{projDetail:VARCHAR}
WHERE proj_no = #{projNo}

mapper.select:
SELECT rec_proj_no, rec_user_id
FROM record_applicant
WHERE rec_proj_no = #{recProjNo:NUMERIC} AND rec_user_id = #{recUserId:VARCHAR}
