mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT proj_no, proj_user_id, proj_division, proj_title, proj_annostart, proj_annoend, proj_startdate, proj_enddate, proj_location, proj_detail, proj_viewcount, round((TO_DATE(concat(proj_enddate,'235959'),'YYYY-MM-DD HH24:MI:SS')- TO_DATE(proj_startdate,'YYYY-MM-DD HH24:MI:SS'))) as proj_expectdate, round((TO_DATE(concat(proj_annoend,'235959'),'YYYY-MM-DD HH24:MI:SS')- TO_DATE(proj_annostart,'YYYY-MM-DD HH24:MI:SS'))) as proj_dday FROM project
AND proj_title like '%' || #{searchKeyword} ||'%'
AND proj_location like '%' || #{searchKeyword} ||'%'
) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT proj_no, proj_user_id, proj_division, proj_title, proj_annostart, proj_annoend, proj_startdate, proj_enddate, proj_location, proj_detail, proj_viewcount
					FROM project
					
						
							
								proj_title = #{searchKeyword}
							
							
								proj_location = #{searchKeyword}
							
						
					 ) countTable

mapper.update:
UPDATE project
proj_division = #{projDivision:NUMERIC}, proj_title = #{projTitle:VARCHAR}, proj_annoend = #{projAnnoEnd:DATE}, proj_startdate = #{projStartDate:DATE}, proj_enddate = #{projEndDate:DATE}, proj_location = #{projLocation:VARCHAR}, proj_detail = #{projDetail:VARCHAR}
WHERE proj_no = #{projNo}
